import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'https://jsonplaceholder.typicode.com/posts';

const House = () => {
  const [houses, setHouses] = useState([]);
  const [house, setHouse] = useState({ name: '', address: '' });
  const [isEditing, setIsEditing] = useState(false);

  // Fetch all houses
  useEffect(() => {
    axios.get(API_URL)
      .then(response => setHouses(response.data))
      .catch(error => console.error('There was an error fetching the houses!', error));
  }, []);

  // Handle form changes
  const handleChange = (e) => {
    setHouse({
      ...house,
      [e.target.name]: e.target.value
    });
  };

  // Handle form submit
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`${API_URL}/${house.id}`, house)
        .then(response => {
          setHouses(houses.map(h => h.id === house.id ? response.data : h));
          setIsEditing(false);
          setHouse({ name: '', address: '' });
        })
        .catch(error => console.error('There was an error updating the house!', error));
    } else {
      axios.post(API_URL, house)
        .then(response => {
          console.log('New house added:', response.data); // Debug log
          setHouses([...houses, response.data]); // Add the new house to the state
          setHouse({ name: '', address: '' }); // Reset the form
        })
        .catch(error => console.error('There was an error creating the house!', error));
    }
  };

  // Edit a house
  const editHouse = (house) => {
    setHouse(house);
    setIsEditing(true);
  };

  // Delete a house
  const deleteHouse = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(() => setHouses(houses.filter(h => h.id !== id)))
      .catch(error => console.error('There was an error deleting the house!', error));
  };

  return (
    <div>
      <h1>Houses</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={house.name}
          onChange={handleChange}
          placeholder="Name"
          required
        />
        <input
          type="text"
          name="address"
          value={house.address}
          onChange={handleChange}
          placeholder="Address"
          required
        />
        <button type="submit">{isEditing ? 'Update' : 'Add'}</button>
      </form>
      <ul>
        {houses.map(h => (
          <li key={h.id}>
            {h.name} - {h.address}
            <button onClick={() => editHouse(h)}>Edit</button>
            <button onClick={() => deleteHouse(h.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default House;
