import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'https://jsonplaceholder.typicode.com/posts'; // URL for API requests

const House = () => {
  const [houses, setHouses] = useState([]); // Start with an empty array
  const [house, setHouse] = useState({ title: '', body: '' }); // Initial state for new house
  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    setHouse({
      ...house,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`${API_URL}/${house.id}`, house)
        .then(response => {
          setHouses(houses.map(h => h.id === house.id ? response.data : h));
          setIsEditing(false);
          setHouse({ title: '', body: '' });
        })
        .catch(error => {
          console.error('There was an error updating the house!', error);
          alert('Error updating house. Check console for details.');
        });
    } else {
      axios.post(API_URL, house)
        .then(response => {
          setHouses([...houses, response.data]);
          setHouse({ title: '', body: '' });
        })
        .catch(error => {
          console.error('There was an error creating the house!', error);
          alert('Error creating house. Check console for details.');
        });
    }
  };

  const editHouse = (house) => {
    setHouse(house);
    setIsEditing(true);
  };

  const deleteHouse = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(() => setHouses(houses.filter(h => h.id !== id)))
      .catch(error => {
        console.error('There was an error deleting the house!', error);
        alert('Error deleting house. Check console for details.');
      });
  };

  return (
    <div>
      <h1>Houses</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={house.title}
          onChange={handleChange}
          placeholder="Title"
          required
        />
        <input
          type="text"
          name="body"
          value={house.body}
          onChange={handleChange}
          placeholder="Body"
          required
        />
        <button type="submit">{isEditing ? 'Update' : 'Add'}</button>
      </form>
      <ul>
        {houses.map(h => (
          <li key={h.id}>
            {h.title} - {h.body}
            <button onClick={() => editHouse(h)}>Edit</button>
            <button onClick={() => deleteHouse(h.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default House;
